import React, {Component} from 'react'

export default class NoteErrorBoundries extends Component{
    constructor(props){
        super(props);
        this.state = {
            hasError: false
        }
    }
    static getDerivedStateFromError(error){
        return ( {hasError: true} );
    }

    render(){
        if (this.state.hasError){
            return(
                <h2>Something went wrong and its probably your fault. Try again later!</h2>
            )
        }
        return this.props.children
    }
}
